import React, { useEffect, useState, useRef } from "react";
import { makeStyles, createStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import InputBase from "@material-ui/core/InputBase";
import IconButton from "@material-ui/core/IconButton";
import SearchIcon from "@material-ui/icons/Search";

const useStyles = makeStyles((theme) =>
  createStyles({
    root: {
      padding: "2px 4px",
      display: "flex",
      alignItems: "center",
      width: 400,
    },
    input: {
      marginLeft: theme.spacing(1),
      flex: 1,
    },
    iconButton: {
      padding: 10,
    },
    divider: {
      height: 28,
      margin: 4,
    },
  })
);

const CoreSearchBar = (props) => {
  const classes = useStyles();

  const [display, setDisplay] = useState(false);
  const [search, setSearch] = useState("");



  const updateSearchResult = (res) => {
    setSearch(res);
    setDisplay(false);
  };

  return (
    <Paper component="form" variant="outlined" className={classes.root}>
      <InputBase
        className={classes.input}
        placeholder="Search"
        inputProps={{ "aria-label": "search" }}
        searchData={props.searchData}
        value={search}
        onClick={() => setDisplay(!display)}
        onChange={(event) => setSearch(event.target.value)}
      />
      {display && (
        <div className="autoContainer">
          {props.searchData
            .filter((value) => value.indexOf(search.toLowerCase()) > -1)
            .map((value, i) => {
              return (
                <div
                  onClick={() => updateSearchResult(value.name)}
                  className="option"
                  key={i}
                  tabIndex="0"
                >
                  {value}
                </div>
              );
            })}
        </div>
      )}
      <IconButton
        type="submit"
        className={classes.iconButton}
        aria-label="search"
        color="primary"
      >
        <SearchIcon />
      </IconButton>
    </Paper>
  );
};

export default CoreSearchBar;
