import React from "react";
import PropTypes from "prop-types";
import IconButton from "@material-ui/core/IconButton";
import Input from "@material-ui/core/Input";
import Paper from "@material-ui/core/Paper";
import SearchIcon from "@material-ui/icons/Search";
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) =>
  createStyles({
    root: {
      height: theme.spacing(6),
      display: "flex",
      justifyContent: "space-between",
    },
    iconButton: {
      color: theme.palette.action.active,
      transform: "scale(1, 1)",
      transition: theme.transitions.create(["transform", "color"], {
        duration: theme.transitions.duration.shorter,
        easing: theme.transitions.easing.easeInOut,
      }),
    },
    iconButtonHidden: {
      transform: "scale(0, 0)",
      "& > $icon": {
        opacity: 0,
      },
    },
    searchIconButton: {
      marginRight: theme.spacing(-6),
    },
    icon: {
      transition: theme.transitions.create(["opacity"], {
        duration: theme.transitions.duration.shorter,
        easing: theme.transitions.easing.easeInOut,
      }),
    },
    input: {
      width: "100%",
    },
    searchContainer: {
      margin: "auto 16px",
      width: `calc(100% - ${theme.spacing(6 + 4)}px)`, // 6 button + 4 margin
    },
  })
);

const SearchBar = React.forwardRef(
  (
    {
      cancelOnEscape,
      disabled,
      onCancelSearch,
      onRequestSearch,
      searchIcon,
      style,
      ...props
    },
    ref
  ) => {
    const inputRef = React.useRef();
    const [value, setValue] = React.useState(props.value);

    React.useEffect(() => {
      setValue(props.value);
    }, [props.value]);

    const handleInput = React.useCallback(
      (e) => {
        setValue(e.target.value);
        if (props.onChange) {
          props.onChange(e.target.value);
        }
      },
      [props.onChange]
    );

    const handleCancel = React.useCallback(() => {
      setValue("");
      if (onCancelSearch) {
        onCancelSearch();
      }
    }, [onCancelSearch]);

    const handleRequestSearch = React.useCallback(() => {
      if (onRequestSearch) {
        onRequestSearch(value);
      }
    }, [onRequestSearch, value]);

    const handleKeyUp = React.useCallback(
      (e) => {
        if (e.charCode === 13 || e.key === "Enter") {
          handleRequestSearch();
        } else if (
          cancelOnEscape &&
          (e.charCode === 27 || e.key === "Escape")
        ) {
          handleCancel();
        }
        if (props.onKeyUp) {
          props.onKeyUp(e);
        }
      },
      [handleRequestSearch, cancelOnEscape, handleCancel, props.onKeyUp]
    );

    return (
      <Paper className={classes.root} style={style}>
        <div className={classes.searchContainer}>
          <Input
            {...props}
            inputRef={inputRef}
            value={value}
            onChange={handleInput}
            onKeyUp={handleKeyUp}
            fullWidth
            className={classes.input}
            disableUnderline
            disabled={disabled}
          />
        </div>
        <IconButton
          onClick={handleRequestSearch}
          className={
            (classes.iconButton,
            classes.searchIconButton,
            {
              [classes.iconButtonHidden]: value !== "",
            })
          }
          disabled={disabled}
        >
          {React.cloneElement(searchIcon, {
            classes: { root: classes.icon },
          })}
        </IconButton>
        <IconButton
          onClick={handleCancel}
          className={
            (classes.iconButton,
            {
              [classes.iconButtonHidden]: value === "",
            })
          }
          disabled={disabled}
        >
          {React.cloneElement(closeIcon, {
            classes: { root: classes.icon },
          })}
        </IconButton>
      </Paper>
    );
  }
);

SearchBar.defaultProps = {
  className: "",
  disabled: false,
  placeholder: "Search",
  searchIcon: <SearchIcon />,
  style: null,
  value: "",
};

SearchBar.propTypes = {
  /** Whether to clear search on escape */
  cancelOnEscape: PropTypes.bool,
  /** Override or extend the styles applied to the component. */
  classes: PropTypes.object.isRequired,
  /** Custom top-level class */
  className: PropTypes.string,
  /** Override the close icon. */
  disabled: PropTypes.bool,
  /** Fired when the search is cancelled. */
  onCancelSearch: PropTypes.func,
  /** Fired when the text value changes. */
  onChange: PropTypes.func,
  /** Fired when the search icon is clicked. */
  onRequestSearch: PropTypes.func,
  /** Sets placeholder text for the embedded text field. */
  placeholder: PropTypes.string,
  /** The value of the text field. */
  value: PropTypes.string,
};

export default withStyles(styles)(SearchBar);
