import React from "react";
import Dialog from "@material-ui/core/Dialog";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
import MuiDialogContent from "@material-ui/core/DialogContent";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import Typography from "@material-ui/core/Typography";
import { withStyles } from "@material-ui/core/styles";
import { makeStyles } from "@material-ui/core/styles";
import { ThemeProvider } from "@material-ui/styles";
import theme from "../../theme";
import Emoji from "../../core/components/core-emoji";
import { Link } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  button: {
    width: "246px",
    paddingTop: theme.spacing(0),
    paddingBottom: theme.spacing(0),
    marginTop: "15px",
    marginBottom: "25px",
    fontSize: "24px",
    borderRadius: "8px",
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.25)",
  },
  input: {
    marginBottom: "32px",
    width: "327px",
  },
}));

const styles = (theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(5),
  },
  title: {
    fontWeight: 600,
    textAlign: "center",
    paddingTop: "70px",
  },
  body: {
    fontSize: "16px",
  },
  closeButton: {
    position: "absolute",
    right: theme.spacing(1),
    top: theme.spacing(1),
    color: "#251F1C",
  },
});

const DialogTitle = withStyles(styles)((props) => {
  const { children, classes, onClose, ...other } = props;
  return (
    <MuiDialogTitle disableTypography className={classes.title} {...other}>
      <Typography variant="h1">{children}</Typography>
      {onClose ? (
        <IconButton
          aria-label="close"
          className={classes.closeButton}
          onClick={onClose}
        >
          <CloseIcon fontSize="large" />
        </IconButton>
      ) : null}
    </MuiDialogTitle>
  );
});

const DialogContent = withStyles((theme) => ({
  root: {
    paddingTop: theme.spacing(2),
    padding: "30px",
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
  },
}))(MuiDialogContent);

export default function NextStepsModal() {
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  return (
    <ThemeProvider theme={theme}>
      <Dialog justifyContent="center" onClose={handleClose} open={true}>
        <DialogTitle onClose={handleClose}>
          Welcome to FUBU <Emoji symbol="👋" label="wave" />
        </DialogTitle>
        <DialogContent>
          <Link to="/create-a-schedule">
            1. Create a schedule for fridge check-ins
          </Link>
          <Typography variant="p">
            2. Create a fridge check-in/ maintenence form
          </Typography>
          <Typography variant="p">
            3. Add a task to your table of action items
          </Typography>
        </DialogContent>
      </Dialog>
    </ThemeProvider>
  );
}
